mapper.select:
SELECT @a:=@a+1 serial_number, A.* FROM ( SELECT a.case_id, a.case_name, a.administrator, DATE_FORMAT(a.create_date, '%Y-%m-%d') AS create_date, a.description , (select count(*) from evidence_info where case_id = a.case_id) AS evd_cnt FROM case_info a LEFT JOIN case_user_auth b ON a.case_id = b.case_id WHERE 1=1
AND b.id = #{id}
GROUP BY a.case_id ORDER BY a.create_date LIMIT #{offset}, #{pageSize} ) A , (SELECT @a:= 0) AS sn

SELECT count(*) cnt FROM ( SELECT a.* FROM case_info a LEFT JOIN case_user_auth b ON a.case_id = b.case_id WHERE 1=1
AND b.id = #{id}
GROUP BY a.case_id ) A
