mapper.select:
	SELECT  a.file_id as id
	       ,a.name 
	  FROM  file_info a
	       ,partition_info b
	 WHERE  a.parent_id = #{id}
	   AND b.evd_id = #{evd_id}

	   AND  b.par_id =  #{par_id}

	   AND a.par_id = b.par_id
	   AND a.dir_type = 3
  ORDER BY a.name

SELECT name, parent_path, extension
  FROM file_info
 WHERE 1=1
   AND id = #{id}
   AND file_id = #{file_id}
