mapper.select:
		SELECT DATE_FORMAT(regdate, '%Y-%m-%d %H:%i:%s') AS regdate
		     , type
         , name 
		  FROM gps_file
		 WHERE 1=1
       AND case_id = #{case_id}
       AND regdate >= #{sdate}

       AND regdate <= #{edate}

     ORDER BY regdate ASC

SELECT roomno, GROUP_CONCAT(DISTINCT name) AS name, content, DATE_FORMAT(regdate,'%Y-%m-%d %H:%i:%s') AS regdate FROM ( SELECT roomno, name, content, regdate FROM ( SELECT roomno, sender_name AS name, content, regdate FROM communication_data WHERE 1=1
AND regdate >= #{sdate}
AND regdate <= #{edate}
AND roomno IS NOT NULL UNION SELECT roomno, receiver_name AS name, content, regdate FROM communication_data WHERE 1=1
AND regdate >= #{sdate}
AND regdate <= #{edate}
AND roomno IS NOT NULL
) AA ORDER BY regdate DESC LIMIT 18446744073709551615 ) A WHERE 1=1 AND name != '' GROUP BY roomno


SELECT seqno, roomno, app, content, sender, sender_name, receiver
     , receiver_name, msg_type, isdelete, DATE_FORMAT(regdate,'%Y-%m-%d %H:%i:%s') AS regdate
     , case_id
  FROM communication_data 
 WHERE 1=1
   AND case_id = #{case_id}
   AND roomno = #{roomno}
 ORDER BY regdate DESC

 LIMIT ${sdata}, ${pageCnt}
